(55, 1, '2022-10-04 02:35:12', '0000-00-00 00:00:00', 'add_action(\'admin_init\', function () {\r\n    // Redirect any user trying to access comments page\r\n    global $pagenow;\r\n    \r\n    if ($pagenow === \'edit-comments.php\') {\r\n        wp_safe_redirect(admin_url());\r\n        exit;\r\n    }\r\n\r\n    // Remove comments metabox from dashboard\r\n    remove_meta_box(\'dashboard_recent_comments\', \'dashboard\', \'normal\');\r\n\r\n    // Disable support for comments and trackbacks in post types\r\n    foreach (get_post_types() as $post_type) {\r\n        if (post_type_supports($post_type, \'comments\')) {\r\n            remove_post_type_support($post_type, \'comments\');\r\n            remove_post_type_support($post_type, \'trackbacks\');\r\n        }\r\n    }\r\n});\r\n\r\n// Close comments on the front-end\r\nadd_filter(\'comments_open\', \'__return_false\', 20, 2);\r\nadd_filter(\'pings_open\', \'__return_false\', 20, 2);\r\n\r\n// Hide existing comments\r\nadd_filter(\'comments_array\', \'__return_empty_array\', 10, 2);\r\n\r\n// Remove comments page in menu\r\nadd_action(\'admin_menu\', function () {\r\n    remove_menu_page(\'edit-comments.php\');\r\n});\r\n\r\n// Remove comments links from admin bar\r\nadd_action(\'init\', function () {\r\n    if (is_admin_bar_showing()) {\r\n        remove_action(\'admin_bar_menu\', \'wp_admin_bar_comments_menu\', 60);\r\n    }\r\n});', 'Completely Disable Comments', '', 'draft', 'closed', 'closed', '', '', '', '', '2022-10-04 02:35:12', '0000-00-00 00:00:00', '', 0, 'http://test-site-m-social.gk/?post_type=wpcode&p=55', 0, 'wpcode', '', 0),
(56, 1, '2022-10-04 02:36:44', '2022-10-03 23:36:44', '$args2 = array(\r\n			\'posts_per_page\' => 3,\r\n			\'post_type\' => \'news\',\r\n			\'meta_query\' => array(\r\n				\'date_key\' =>array(\r\n					\'key\' => \'eta\',\r\n					\'compare\' => \'EXISTS\',\r\n					\'type\' => \'DATE\',\r\n					)\r\n				),\r\n				\'orderby\' => array(\r\n					\'date_key\' => \'DESC\'\r\n					)\r\n				);\r\n		$query2 = new WP_Query( $args2 ); ?>\r\n\r\n		<?php if ( $query2->have_posts() ) : ?>\r\n\r\n			<div class=\"container text-left\">\r\n  					<div class=\"row row-cols-1 row-cols-md-3 row-cols-xxl-3\">\r\n\r\n		<?php while ( $query2->have_posts() ) : $query2->the_post(); ?>\r\n			\r\n						<div class=\"col card\">\r\n									<?php \r\n										$thumbnail_id = carbon_get_post_meta(get_the_ID(),\'crb_image\');\r\n										$thumbnail_url = wp_get_attachment_image_url( $thumbnail_id, \'full\' );\r\n									?>\r\n									<img src=\"<?php echo $thumbnail_url; ?>\" alt=\"\" />\r\n									<p><?php echo carbon_get_post_meta( get_the_ID(), \'eta\' );  ?></p>\r\n									\r\n			    					<h2 class=\"h4\"><?php the_title() ?></h2>\r\n									<p><?php the_excerpt() ?></p>\r\n									<a class=\"btn btn-primary stretched-link\" href=\"<?php  echo get_permalink() ?>\">More</a>\r\n								</div>\r\n					\r\n		<?php endwhile; ?>\r\n\r\n		</div>\r\n			</div>\r\n\r\n	<?php endif; ?>', 'Latest news', '', 'publish', 'closed', 'closed', '', 'latest-news', '', '', '2022-10-04 18:35:52', '2022-10-04 15:35:52', '', 0, 'http://test-site-m-social.gk/wpcode/latest-news/', 0, 'wpcode', '', 0),
