(68, 1, '2022-10-04 12:00:25', '2022-10-04 09:00:25', '$args3 = array(\r\n			\'posts_per_page\' => 3,\r\n			\'post_type\' => \'product\',\r\n		);\r\n\r\n		$query3 = new WP_Query( $args3 ); ?>\r\n\r\n		<?php if ( $query3->have_posts() ) : ?>\r\n\r\n			<div class=\"container text-left\">\r\n  					<div class=\"row row-cols-1 row-cols-md-3 row-cols-xxl-3\">\r\n\r\n		<?php while ( $query3->have_posts() ) : $query3->the_post(); ?>\r\n			\r\n						<div class=\"col\">\r\n									<?php \r\n						 \r\n						while(have_posts()): the_post(); ?>\r\n\r\n								<div class=\"col p-5\">\r\n									<?php $slides = carbon_get_post_meta(get_the_ID(), \'crb_media_gallery\');\r\n										foreach ($slides as $slide):\r\n									?>\r\n									<img src=\"<?php echo wp_get_attachment_image_url($slide, \'full\'); ?>\" alt=\"Image\">\r\n									<?php endforeach; ?>\r\n\r\n			    						<h2 class=\"h4\"><?php the_title() ?></h2>\r\n										<p><?php the_excerpt() ?></p>\r\n								</div>\r\n			    				\r\n						<?php	\r\n						endwhile;\r\n			 			?>\r\n\r\n		</div>\r\n			</div>\r\n\r\n	<?php endif; ?>', 'Popular goods', '', 'trash', 'closed', 'closed', '', '__trashed', '', '', '2022-10-04 12:00:25', '2022-10-04 09:00:25', '', 0, 'http://test-site-m-social.gk/?post_type=wpcode&#038;p=68', 0, 'wpcode', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(69, 1, '2022-10-04 12:00:35', '2022-10-04 09:00:35', '$args3 = array(\r\n			\'posts_per_page\' => 3,\r\n			\'post_type\' => \'product\',\r\n			\'meta_query\' => array(\r\n				\'price\' =>array(\r\n					\'key\' => \'crb_phone_number\',\r\n					\'compare\' => \'EXISTS\',\r\n					\'type\' => \'NUMERIC\',\r\n					)\r\n				),\r\n				\'orderby\' => array(\r\n					\'price\' => \'ASC\'\r\n					)\r\n				);\r\n		$query3 = new WP_Query( $args3 ); ?>\r\n\r\n		<?php if ( $query3->have_posts() ) : ?>\r\n\r\n			<div class=\"container text-left\">\r\n  					<div class=\"row row-cols-1 row-cols-md-3 row-cols-xxl-3\">\r\n\r\n		<?php while ( $query3->have_posts() ) : $query3->the_post(); ?>\r\n			\r\n						<div class=\"col card\">\r\n			\r\n								<?php $slides = carbon_get_post_meta(get_the_ID(), \'crb_media_gallery\');\r\n									foreach ($slides as $slide):\r\n								?>\r\n								<img src=\"<?php echo wp_get_attachment_image_url($slide, \'full\'); ?>\" alt=\"Image\">\r\n								<?php endforeach; ?>\r\n			    				<h2 class=\"h4\"><?php the_title() ?></h2>\r\n								<p><?php the_excerpt() ?></p>\r\n								<h4 class=\"text-danger\"><span>Цена:</span> <?php echo carbon_get_the_post_meta(\'crb_phone_number\'); ?> <span>руб</span></h4>\r\n								<a class=\"btn btn-primary stretched-link\" href=\"<?php  echo get_permalink() ?>\">More</a>\r\n						</div>\r\n					\r\n		<?php endwhile; ?>\r\n\r\n		</div>\r\n			</div>\r\n\r\n	<?php endif; ?>', 'Popular goods', '', 'publish', 'closed', 'closed', '', 'popular-goods', '', '', '2022-10-04 18:25:18', '2022-10-04 15:25:18', '', 0, 'http://test-site-m-social.gk/?post_type=wpcode&#038;p=69', 0, 'wpcode', '', 0),
